import { rangeIntersect } from "@brendangooch/maths";
export function rectangleInRectangle(r1x, r1y, r1w, r1h, r2x, r2y, r2w, r2h) {
    return (rangeIntersect(r1x - (r1w / 2), r1x + (r1w / 2), r2x - (r2w / 2), r2x + (r2w / 2)) &&
        rangeIntersect(r1y - (r1h / 2), r1y + (r1h / 2), r2y - (r2h / 2), r2y + (r2h / 2)));
}
