export class ImageSprite {
    constructor(img, numCols, numRows) {
        this.current = { col: 1, row: 1 };
        this.img = img;
        this.numCols = numCols;
        this.numRows = numRows;
    }
    get image() {
        return this.img;
    }
    get x() {
        return (this.current.col - 1) * this.width;
    }
    get y() {
        return (this.current.row - 1) * this.height;
    }
    get width() {
        return this.img.width / this.numCols;
    }
    get height() {
        return this.img.height / this.numRows;
    }
    column(column) {
        this.current.col = column;
        return this;
    }
    row(row) {
        this.current.row = row;
        return this;
    }
}
