export class Angle {
    constructor() {
        this.angle = 0;
        return this;
    }
    init(turns) {
        this.angle = turns;
        return this;
    }
    get turns() {
        return this.angle;
    }
    get degrees() {
        return this.turns * 360;
    }
    get radians() {
        return this.turns * Math.PI * 2;
    }
    set turns(turns) {
        this.angle = turns;
    }
    set degrees(degrees) {
        this.angle = degrees / 360;
    }
    set radians(radians) {
        this.angle = radians / (Math.PI * 2);
    }
}
