export class TimeSpeedDistance {
    constructor() {
        this._time = 0;
        this._speed = 0;
        this._distance = 0;
        return this;
    }
    get time() {
        if (this._distance === 0)
            return 0;
        if (this._speed === 0)
            return 0;
        return this._distance / this._speed;
    }
    get speed() {
        if (this._distance === 0)
            return 0;
        if (this._time === 0)
            return 0;
        return this._distance / this._time;
    }
    get distance() {
        if (this._speed === 0)
            return 0;
        if (this._time === 0)
            return 0;
        return this._speed * this._time;
    }
    setTime(ms) {
        this._time = ms;
        return this;
    }
    setSpeed(unitsPerMs) {
        this._speed = unitsPerMs;
        return this;
    }
    setDistance(units) {
        this._distance = units;
        return this;
    }
}
