import {} from "./index.js";
import { getSuitFromType } from "./get-suit-from-type.js";
export class Suit {
    constructor(type) {
        this.type = type;
    }
    get number() {
        return getSuitFromType(this.type);
    }
    get name() {
        switch (this.number) {
            case 0:
                return 'joker';
            case 1:
                return 'club';
            case 2:
                return 'heart';
            case 3:
                return 'spade';
            case 4:
                return 'diamond';
        }
        return 'undefined';
    }
}
