import { SimpleGameLoop } from "@brendangooch/simple-game-loop";
import { SimpleRenderer } from "@brendangooch/simple-renderer";
export class SimpleGameEngine {
    constructor(canvas) {
        this.gameLoop = new SimpleGameLoop();
        this.running = false;
        this.renderer = new SimpleRenderer(canvas);
    }
    loop(objects) {
        objects.forEach(obj => this.gameLoop.add(obj));
    }
    render(objects) {
        objects.forEach(obj => this.renderer.add(obj));
    }
    start() {
        if (!this.running) {
            this.gameLoop.start();
            this.renderer.start();
            this.running = true;
        }
    }
    stop() {
        if (this.running) {
            this.gameLoop.stop();
            this.renderer.stop();
            this.running = false;
        }
    }
    toggle() {
        if (!this.running)
            this.start();
        else
            this.stop();
    }
}
