export class SimpleRenderer {
    constructor(canvas) {
        this.running = false;
        this.objectsToRender = [];
        this.intervalID = 0;
        this.canvas = canvas;
    }
    add(obj) {
        this.objectsToRender.push(obj);
    }
    start() {
        if (!this.running) {
            this.loadInterval();
            this.running = true;
        }
    }
    stop() {
        if (this.running) {
            clearInterval(this.intervalID);
            this.running = false;
        }
    }
    render() {
        this.canvas.clear();
        this.objectsToRender.forEach(obj => obj.render(this.canvas));
    }
    loadInterval() {
        this.intervalID = setInterval(this.render.bind(this), 1000 / SimpleRenderer.FPS);
    }
}
SimpleRenderer.FPS = 60;
