import AbstractCanvasComponent from "./abstract-canvas-component.js";
export default class CanvasColor extends AbstractCanvasComponent {
    fill(color) {
        this.ctx.fillStyle = color;
    }
    stroke(color) {
        this.ctx.strokeStyle = color;
    }
    both(color) {
        this.fill(color);
        this.stroke(color);
    }
}
